import React, { useEffect, useState } from 'react';
import { Spinner } from 'react-bootstrap';
import './Comments.css';

const Comments = ({ id }) => {
    const [userComments, setUserComments] = useState({});
    const [loading, setLoading] = useState(true);
    const { comment } = userComments;


    useEffect(() => {
        //setLoading(true)
        fetch(`http://localhost:5000/posts/${id}`)
            .then(res => res.json())
            .then(data => setUserComments(data))
            .finally(() => setLoading(false))
    }, [userComments])



    if (loading) {
        return <Spinner animation="border" variant="primary" />
    }
    else {
        return (
            <>
                {
                    comment.map(singleComment => (
                        <div className="my-3 d-flex">
                            <div className="comment-profile-img rounded-circle">
                                <img className="img-fluid comment-profile-img rounded-circle" src={`data:image/png;base64,${singleComment?.commentPersonImg}`} alt="Profile Image" />
                            </div>
                            <div className="comment-desc ms-2 py-2 px-3">
                                <p style={{ fontSize: '14px', fontWeight: 600 }} className="m-0">{singleComment?.commentPersonName}</p>
                                <p className="m-0">{singleComment?.commentDesc}</p>
                            </div>
                        </div>
                    ))
                }
            </>
        );
    }

};

export default Comments;
