import React from 'react';
import { Col, Container, Row } from 'react-bootstrap';
import { BsCamera } from 'react-icons/bs';
import useAuth from '../../hooks/useAuth';
import Header from '../Shared/Header/Header'
import './UserProfile.css';

const UserProfile = () => {
    const { user } = useAuth();
    return (
        <div>
            <Header />
            <div className="">
                <Container className="user-cover-img-container" fluid>
                    <Row>
                        <Col className="m-0 p-0">
                            <img className="img-fluid w-100" src="http://www.wpkixx.com/html/winku-dark/images/resources/timeline-1.jpg" alt="Cover_Photo" />
                        </Col>
                    </Row>
                </Container>
                <div className="user-profile-img-container" fluid>
                    <div className="user-profile-img rounded-circle">
                        <img className="img-fluid rounded-circle" src="http://www.wpkixx.com/html/winku-dark/images/resources/user-avatar.jpg" alt="Profile_Photo" />
                        <label for="profile-img" className="profile-img-icon rounded-circle d-flex align-items-center justify-content-center">
                            <BsCamera style={{ color: '#088dcd', fontSize: '22px' }} />
                            <input style={{ display: 'none' }} id="profile-img" accept="image/*" type="file" />
                        </label>
                    </div>
                </div>
            </div>

        </div >
    );
};

export default UserProfile;
