import { useState } from "react";


const useLikes = (id) => {
    const [like, setLike] = useState(0);


    const handleLike = () => {
        const newLike = like + 1;
        if (newLike > 0) {
            if (newLike === 1) {
                setLike(newLike)
            }
        };

        if (newLike > 1) {
            setLike(like - 1)
        }
    };

    return {
        like,
        handleLike
    }
};

export default useLikes;
