import { useState } from "react";
import useAuth from "./useAuth";
import useUserInfo from "./useUserInfo";


const useLikes = ({ id }) => {
    //const { user } = useAuth();
    const { userInformation } = useUserInfo();
    const [like, setLike] = useState(0);


    const handleLike = (id) => {
        // if (userInformation) {

        // }
        const newLike = like + 1;
        if (newLike > 0) {
            if (newLike === 1) {
                setLike(newLike)
            }
        };
    };
    const handleUnLike = () => {
        if (like > 1) {
            setLike(like - 1)
        }
    }
    const likedDetail =
    {
        likedPersonName: userInformation?.name,
        likedPersonEmail: userInformation?.email,
        likedPersonImage: userInformation?.userProfileImage,
        like: like
    }

    return {
        likedDetail,
        like,
        handleLike,
        handleUnLike
    }
};

export default useLikes;
