import React, { useEffect, useState } from 'react';
import { Col, Container, Row } from 'react-bootstrap';
import { BsCamera } from 'react-icons/bs';
import useAuth from '../../hooks/useAuth';
import Header from '../Shared/Header/Header';
import {
    Switch,
    Route,
    useRouteMatch,
    NavLink
} from "react-router-dom";
import TimeLine from './TimeLine/TimeLine';
import Photos from './Photos/Photos';
import NewsFeedLeftTop from '../Home/NewsFeed/NewsFeedLeftTop/NewsFeedLeftTop';
import NewsFeedRecentActivity from '../Home/NewsFeed/NewsFeedRecentActivity/NewsFeedRecentActivity';
import NewsFeedFollower from '../Home/NewsFeed/NewsFeedFollower/NewsFeedFollower';
import NewsFeedRightCenter from '../Home/NewsFeed/NewsFeedRightCenter/NewsFeedRightCenter';
import NewsFeedRightBottom from '../Home/NewsFeed/NewsFeedRightBottom/NewsFeedRightBottom';
import About from './About/About';
import './UserProfile.css';

const UserProfile = () => {
    const { user } = useAuth();
    const [coverImage, setCoverImage] = useState(null)
    const [profileImage, setProfileImage] = useState(null)
    const [userInformation, setUserInformation] = useState({})
    let { path, url } = useRouteMatch();


    useEffect(() => {
        fetch(`http://localhost:5000/users?email=${user?.email}`)
            .then(res => res.json())
            .then(data => setUserInformation(data[0]))
    }, [])

    console.log(userInformation);
    const handleCoverImage = e => {
        setCoverImage(e.target.files[0])
    }
    const handleProfileImage = e => {
        setProfileImage(e.target.files[0])
    }

    const formData = new FormData();
    formData.append('profileImage', profileImage);
    formData.append('coverImage', coverImage);

    //const emnei = () => {
    useEffect(() => {
        fetch(`http://localhost:5000/users?email=${user?.email}`, {
            method: 'PUT',
            body: formData
        })
            .then(res => res.json())
            .then(data => {
                console.log(data);
            })
    }, [formData])

    //}


    return (
        <div>
            <Header />
            <div className="user-profile-info">
                <Container className="user-cover-img-container" fluid>
                    <Row>
                        <Col className="m-0 p-0">
                            <img className="img-fluid w-100 h-100" src={coverImage} alt="Cover_Photo" />
                        </Col>
                    </Row>
                </Container>
                <div className="user-profile-info-img-container  d-flex align-items-center justify-content-between" fluid>
                    <div className="user-profile-info-img rounded-circle">
                        <img className="img-fluid rounded-circle" src={userInformation?.userProfileImage} alt="Profile_Photo" />
                        <label for="profile-img" className="user-profile-info-img-icon rounded-circle d-flex align-items-center justify-content-center">
                            <BsCamera className="profile-camera-icon" />
                            <input onChange={handleProfileImage} style={{ display: 'none' }} id="profile-img" accept="image/*" type="file" />
                        </label>
                    </div>
                    <label for="cover-img" className="edit-cover-photo-btn text-white d-flex align-items-center justify-content-center">
                        <input onChange={handleCoverImage} style={{ display: 'none' }} id="cover-img" accept="image/*" type="file" />
                        <BsCamera className="cover-camera-icon" />
                        <span className="ms-3">Edit Cover Photo</span>
                    </label>
                    <h1>{userInformation?.name}</h1>
                    {/* <button onClick={emnei}>Checking</button> */}
                </div>
                <Container fluid>
                    <Row className="user-info-menubar">
                        <Col className="user-info-menubar-left d-flex align-items-center justify-content-end" md={4}>
                            <div>
                                <h5 style={{ color: '#088dcd' }} className="text-capitalize m-0">{user.displayName}</h5>
                                <div className="text-end">
                                    <p className="text-white m-0" style={{ fontWeight: 600, fontSize: '12px' }}>Add Bio</p>
                                </div>
                            </div>
                        </Col>
                        <Col className="d-flex align-items-center" md={7}>
                            <NavLink className="text-decoration-none user-info-nav-link text-white" to={`${url}`}>Time Line</NavLink>
                            <NavLink activeStyle={{
                                color: "#088dcd",
                                borderBottom: '2px solid #088dcd',
                                borderRadius: 0
                            }} className="text-decoration-none user-info-nav-link text-white" to={`${url}/photos`}>Photos</NavLink>
                            <NavLink activeStyle={{
                                color: "#088dcd",
                                borderBottom: '2px solid #088dcd',
                                borderRadius: 0
                            }} className="text-decoration-none user-info-nav-link text-white" to={`${url}/videos`}>Videos</NavLink>
                            <NavLink activeStyle={{
                                color: "#088dcd",
                                borderBottom: '2px solid #088dcd',
                                borderRadius: 0
                            }} className="text-decoration-none user-info-nav-link text-white" to={`${url}/friends`}>Friends</NavLink>
                            <NavLink activeStyle={{
                                color: "#088dcd",
                                borderBottom: '2px solid #088dcd',
                                borderRadius: 0
                            }} className="text-decoration-none user-info-nav-link text-white" to={`${url}/groups`}>Groups</NavLink>
                            <NavLink activeStyle={{
                                color: "#088dcd",
                                borderBottom: '2px solid #088dcd',
                                borderRadius: 0
                            }} className="text-decoration-none user-info-nav-link text-white" to={`${url}/about`}>About</NavLink>
                            <NavLink activeStyle={{
                                color: "#088dcd",
                                borderBottom: '2px solid #088dcd',
                                borderRadius: 0
                            }} className="text-decoration-none user-info-nav-link text-white" to={`${url}/more`}>More</NavLink>
                        </Col>
                    </Row>
                    <Row className="user-info-main-container">
                        <Col md={3}>
                            <Row className="mt-3">
                                <Col className="user-info-left-about-sidebar">
                                    <About />
                                </Col>
                            </Row>
                            <Row className="mt-3">
                                <Col className="user-info-left-sidebar">
                                    <NewsFeedLeftTop />
                                </Col>
                            </Row>
                            <Row className="mt-3">
                                <Col className="user-info-left-sidebar">
                                    <NewsFeedRecentActivity />
                                </Col>
                            </Row>
                            <Row className="mt-3">
                                <Col className="user-info-left-sidebar">
                                    <NewsFeedFollower />
                                </Col>
                            </Row>
                        </Col>
                        <Col className="menubar-center-main-container p-0" md={6}>
                            <Switch>
                                <Route exact path={path}>
                                    <TimeLine />
                                </Route>
                                <Route path={`${path}/photos`}>
                                    <Photos />
                                </Route>
                            </Switch>
                        </Col>
                        <Col md={3}>
                            <Row className="mt-3">
                                <Col className="user-info-left-sidebar">
                                    <NewsFeedRightCenter />
                                </Col>
                            </Row>
                            <Row className="mt-3">
                                <Col className="user-info-left-sidebar">
                                    <NewsFeedRightBottom />
                                </Col>
                            </Row>
                        </Col>
                    </Row>
                </Container>
            </div>

        </div >
    );
};

export default UserProfile;
