import { useState } from "react";
import useAuth from "./useAuth";


const useLikes = () => {
    const { user } = useAuth();
    const [like, setLike] = useState(0);


    const handleLike = () => {
        const newLike = like + 1;
        if (newLike > 0) {
            if (newLike === 1) {
                setLike(newLike)
            }
        };

        if (newLike > 1) {
            setLike(like - 1)
        }
    };
    const likedDetail = {
        likedPersonName: user?.displayName,
        likedPersonEmail: user?.email,
        like: like
    }
    return {
        likedDetail,
        handleLike
    }
};

export default useLikes;
