import React, { useEffect, useState } from 'react';
import { Col, Row } from 'react-bootstrap';
import { BiLike } from 'react-icons/bi';
import { BsCamera, BsThreeDots } from 'react-icons/bs';
import { FaRegCommentDots } from 'react-icons/fa';
import { IoIosShareAlt } from 'react-icons/io';
import { AiFillLike } from 'react-icons/ai';
import useLikes from '../../../../hooks/useLikes';
import './UsersPost.css';
import useAuth from '../../../../hooks/useAuth';
import useUserInfo from '../../../../hooks/useUserInfo';
import Comments from './Comments/Comments';
//import UserPost from './UserPost/UserPost';

const UsersPost = () => {
    //const { user } = useAuth();
    const { userInformation } = useUserInfo();
    const { handleLike, likedDetail, like, handleUnLike } = useLikes();
    const [userPosts, setUserPosts] = useState([]);
    const [comment, setComment] = useState('');

    useEffect(() => {
        fetch('http://localhost:5000/posts')
            .then(res => res.json())
            .then(data => setUserPosts(data))
    }, [userPosts])




    const handlePostLike = id => {
        handleLike(id);
        fetch(`http://localhost:5000/posts/like/${id}`, {
            method: 'PUT',
            headers: { 'content-type': 'application/json' },
            body: JSON.stringify(likedDetail)
        })
            .then(res => res.json())
            .then(data => {

            })

    }


    const handleComment = e => {
        setComment(e.target.value);
    }

    const handleCommentPost = id => {
        const allComment =
        {
            name: userInformation?.name,
            email: userInformation?.email,
            commentPersonImg: userInformation?.userProfileImage,
            comment: comment
        }


        fetch(`http://localhost:5000/posts/${id}`, {
            method: 'PUT',
            headers: { 'content-type': 'application/json' },
            body: JSON.stringify(allComment)
        })
            .then(res => res.json())
            .then(data => {
                console.log(data);
            })
    }


    //console.log(userPosts);


    // handle textarea auto resize
    // const textarea = document.getElementById('comment-field')
    // textarea.addEventListener('keyup', e => {
    //     textarea.style.height = `40px`;
    //     const textareaScrollbar = e.target.scrollHeight;
    //     textarea.style.height = `${textareaScrollbar}px`
    // });



    return (
        <div style={{ display: 'flex', flexDirection: "column-reverse" }}>
            {
                userPosts.map(userPost => (
                    // <UserPost
                    //     userPost={userPost}
                    //     key={userPost?._id}
                    // >

                    // </UserPost>

                    <div key={userPost?._id} className="p-4 post-card mb-3">
                        <div className="d-flex justify-content-between">
                            <div className="post-top mb-4 d-flex align-items-center">
                                <div className="user-profile-img rounded-circle me-3">
                                    <img className="img-fluid rounded-circle" src={`data:image/png;base64,${userPost?.userProfileImage}`} alt="User_Profile_Picture" />
                                </div>
                                <div>
                                    <h6 className="m-0">
                                        {userPost?.name}
                                    </h6>
                                    <p className="m-0"><small>Published: {userPost?.date}</small></p>
                                </div>
                            </div>
                            <div>
                                <button style={{ color: '#ddd' }} className="bg-transparent border-0 m-0"><BsThreeDots /></button>
                            </div>
                        </div>
                        <div className="post-desc mb-4">
                            <p>{userPost?.postDesc}</p>
                        </div>
                        <div className="post-img mb-4">
                            <img className="img-fluid w-100" src={`data:image/png;base64,${userPost?.image}`} alt="Post_Image" />
                        </div>
                        <div className="post-reacts mb-4">
                            <div className="d-flex justify-content-between">
                                <p className="total-likes d-flex align-items-center"><AiFillLike className="me-1" /> {like}</p>
                                <div className="d-flex align-items-center">
                                    <p className="total-likes d-flex align-items-center me-2">0 Comments</p>
                                    <p className="total-likes d-flex align-items-center">0 Shares</p>
                                </div>
                            </div>
                            <hr className="m-0 post-hr" />
                            <Row>


                                <Col className="d-flex align-items-center my-1 justify-content-center post-react-btn-container" md={4} xs={4}>
                                    <div>
                                        <button className="w-100 border-0 post-react-btn d-flex align-items-center">
                                            {like ? <span onClick={() => handleUnLike()} style={{ color: '#088dcd' }} className="d-flex align-items-center">
                                                <AiFillLike className="like-btn me-2 liked-btn" />Like
                                            </span>
                                                :
                                                <span onClick={() => handlePostLike(userPost?._id)} className="d-flex align-items-center">
                                                    <BiLike className="like-btn me-2" />Like
                                                </span>}
                                        </button>
                                    </div>
                                </Col>


                                <Col className="d-flex align-items-center my-1 justify-content-center post-react-btn-container" md={4} xs={4}>
                                    <div>
                                        <button className="w-100 border-0 post-react-btn d-flex align-items-center">
                                            <span><FaRegCommentDots className="like-btn me-2" /></span>
                                            Comment
                                        </button>
                                    </div>
                                </Col>
                                <Col className="d-flex align-items-center my-1 justify-content-center post-react-btn-container" md={4} xs={4}>
                                    <div>
                                        <button className="w-100 border-0 post-react-btn d-flex align-items-center">
                                            <IoIosShareAlt className="like-btn me-2" />
                                            Share
                                        </button>
                                    </div>
                                </Col>
                            </Row>
                            <hr className="m-0 post-hr" />
                        </div>
                        <div>
                            {userPost?.comment && <Comments id={userPost?._id} />}
                        </div>
                        <div className="post-comment mb-2">
                            <Row className="">
                                <Col xs={2} md={1}>
                                    <div className="user-comment-profile-img mt-1 rounded-pill">
                                        <img className="img-fluid rounded-circle" src={`data:image/png;base64,${userInformation?.userProfileImage}`} alt="User_Profile_Picture" />
                                    </div>
                                </Col>
                                <Col className="" xs={10} md={11}>
                                    <div className="comment-box-container  d-flex">
                                        <textarea onBlur={handleComment} id="comment-field" rows="4" cols="50" placeholder="Write a Comment" className="bg-transparent text-white pt-2 px-3 border-0 comment-box-filed" />
                                        <div className="text-end align-items-center comment-box-btns d-flex">
                                            <BsCamera className="comment-box-icon" />
                                            <div>
                                                <button onClick={() => handleCommentPost(userPost?._id)} className="border-0 text-white comment-btn">Post</button>
                                            </div>
                                        </div>
                                    </div>
                                </Col>
                            </Row>
                        </div>
                    </div >
                ))
            }
        </div>
    );
};

export default UsersPost;
