import { useState } from "react";

const usePostReact = () => {
    const [like, setLike] = useState(0);

    const handleLike = () => {
        const newLike = like + 1;
        if (newLike > 0) {
            if (newLike === 1) {
                setLike(newLike)
            }
        };

        if (newLike > 1) {
            setLike(like - 1)
        }

        fetch('http://localhost:5000/posts', {
            method: 'PUT',
            headers: { 'content-type': 'application/json' },
            body: JSON.stringify(like)
        })
    };

    return {
        handleLike,
        like
    }
};

export default usePostReact;
