import { useEffect, useState } from "react";
import { getAuth, signInWithPopup, GoogleAuthProvider, signOut, onAuthStateChanged, updateProfile, createUserWithEmailAndPassword, signInWithEmailAndPassword } from "firebase/auth";
import initialization from "../firebase/firebase.init";

initialization();
const useFirebase = () => {
    const [user, setUser] = useState({});
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true);


    const googleProvider = new GoogleAuthProvider();
    const auth = getAuth();

    // HANDLE SIGN IN WITH GOOGLE
    const signInWithGoogle = () => {
        signInWithPopup(auth, googleProvider)
            .then((result) => {
                setUser(result.user);
            }).catch((error) => {
                setError(error.message);
            });
    };


    // HANDLE USER REGISTER WITH EMAIL & PASSWORD ACCOUNT
    const userRegister = (name, email, password, location, history) => {
        setLoading(true)
        createUserWithEmailAndPassword(auth, email, password)
            .then((result) => {
                setUser(result.user);
                setUserName(name)
                const destiny = location?.state?.form || '/';
                history.replace(destiny)
            })
            .catch((error) => {
                setError(error.message);
            })
            .finally(() => setLoading(false));
    }



    // SET USER NAME
    const setUserName = (name) => {
        updateProfile(auth.currentUser, {
            displayName: name
        }).then(() => {
            // Profile updated!
            // ...
        }).catch((error) => {
            // An error occurred
            // ...
        });

    }


    // HANDLE USER LOGIN WITH EMAIL & PASSWORD
    const userLogin = (email, password, location, history) => {
        setLoading(true)
        signInWithEmailAndPassword(auth, email, password, location, history)
            .then((result) => {
                setUser(result.user);
                const destiny = location?.state?.form || '/';
                history.replace(destiny)
            })
            .catch((error) => {
                setError(error.message);
            })
            .finally(() => setLoading(false));
    }


    // SAVE USER BEHAVIOUR
    useEffect(() => {
        setLoading(true)
        const unsubscribe = onAuthStateChanged(auth, (user) => {
            if (user) {
                setUser(user)
            } else {
                setUser({})
            }
            setLoading(false)
        });
        return () => unsubscribe;
    }, []);


    // HANDLE SIGN OUT
    const logOut = () => {
        setLoading(true)
        signOut(auth).then(() => {
            // Sign-out successful.
            setLoading(false)
        }).catch((error) => {
            // An error happened.
            setError(error.message);
        });
    }


    return {
        user,
        error,
        loading,
        signInWithGoogle,
        userRegister,
        userLogin,
        logOut
    }
};

export default useFirebase;
