import { useState } from "react";
import useAuth from "./useAuth";
import useUserInfo from "./useUserInfo";


const useLikes = () => {
    const { user } = useAuth();
    const { userInformation } = useUserInfo();
    const [like, setLike] = useState();


    const handleLike = () => {
        // if (userInformation) {

        // }
        const newLike = like + 1;
        if (newLike > 0) {
            if (newLike === 1) {
                setLike(newLike)
            }
        };

        if (newLike > 1) {
            setLike(like - 1)
        }
    };
    const likedDetail =
    {
        likedPersonName: user?.displayName,
        likedPersonEmail: user?.email,
        like: like
    }

    return {
        likedDetail,
        like,
        handleLike
    }
};

export default useLikes;
