import React, { useState } from 'react';
import { Col, Container, Row } from 'react-bootstrap';
import { NavLink, useLocation, useHistory } from 'react-router-dom';
import useAuth from '../../hooks/useAuth';
import './Register.css';

const Register = () => {
    const [name, setName] = useState('');
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const { userRegister } = useAuth();
    const [gender, setGender] = useState('');

    const location = useLocation();
    const history = useHistory();

    const handleName = e => {
        setName(e.target.value);
    }
    const handleUsername = e => {
        setUsername(e.target.value);
    }
    const handleEmail = e => {
        setEmail(e.target.value);
    }
    const handlePassword = e => {
        setPassword(e.target.value);
    }

    const handleMale = e => {
        setGender(e.target.value)
    }

    const handleFemale = e => {
        setGender(e.target.value)
    }

    const OnSubmitForm = e => {
        e.preventDefault();
        userRegister(name, email, password, username, male, female, location, history);
    }

    return (
        <div>
            <Container fluid>
                <Row>
                    <Col className="log-res-left-container d-flex justify-content-center align-items-center" md={6}>
                        <div className="text-white text-center">
                            <h1>Meet Friends</h1>
                            <p>Welcome to Meet Friends social network site its free to use as long as you want</p>
                            <img className="img-fluid my-5" src="http://www.wpkixx.com/html/winku-dark/images/wink.png" alt="Register_Image" />
                            <p>Follow Us on</p>
                        </div>
                    </Col>
                    <Col className="log-res-right-container d-flex justify-content-center align-items-center" md={6}>
                        <div className="form-container">
                            <h2>Register</h2>
                            <p>Donâ€™t use Meet Friends Yet? <span>Take</span> the tour or <span>Join now</span></p>

                            <form onSubmit={OnSubmitForm}>
                                <input onBlur={handleName} type="text" placeholder="First & Last Name" className="mt-4 py-2  w-100" />
                                <input onBlur={handleUsername} type="text" placeholder="Username" className="mt-4 py-2  w-100" />
                                <input onBlur={handleEmail} type="email" placeholder="Email Address" className="mt-4 py-2  w-100" />
                                <input onBlur={handlePassword} type="password" placeholder="Password" className="mt-4 py-2  w-100" />
                                <Row className="my-4">
                                    <Col md={6}>
                                        <div className="d-flex align-items-center">
                                            <span className="d-flex align-items-center me-2">
                                                <input onChange={handleMale} name="checked" value="male" style={{ width: '13px', height: '13px', verticalAlign: 'middle' }} type="radio" className="me-2" />
                                                <span id="male" style={{ fontSize: '13px', color: '#545454' }}>Male</span>
                                            </span>
                                            <span className="d-flex align-items-center">
                                                <input onChange={handleFemale} name="checked" value="female" style={{ width: '13px', height: '13px', verticalAlign: 'middle' }} type="radio" className="me-2" />
                                                <span id="female" style={{ fontSize: '13px', color: '#545454' }}>Female</span>
                                            </span>
                                        </div>
                                    </Col>
                                    <Col className="p-0" md={6}>
                                        <div className="d-flex align-items-center">
                                            <span className="d-flex align-items-center me-4">
                                                <input name="checked" value="male" style={{ width: '12px', height: '12px', verticalAlign: 'middle' }} type="checkbox" className="me-2" />
                                                <span id="male" style={{ fontSize: '13px', color: '#545454' }}>Accept Terms & Conditions ?</span>
                                            </span>
                                        </div>
                                    </Col>
                                </Row>
                                <button type="submit" className="w-100 border-0 mt-3 text-white py-2">Register</button>
                            </form>
                            <NavLink className="text-center d-block mt-4" to="/login">Already have an account?</NavLink>
                        </div>
                    </Col>
                </Row>
            </Container>
        </div>
    );
};

export default Register;
