import React, { useEffect, useState } from 'react';
import { Col, Row } from 'react-bootstrap';
import { BiLike } from 'react-icons/bi';
import { BsCamera } from 'react-icons/bs';
import { FaRegCommentDots } from 'react-icons/fa';
import { IoIosShareAlt } from 'react-icons/io';
import { AiFillLike } from 'react-icons/ai';
import './UsersPost.css';
import useLikes from '../../../../hooks/useLikes';

const UsersPost = () => {
    const [userPosts, setUserPosts] = useState([]);
    // const [like, setLike] = useState(0);
    const { handleLike, like } = useLikes();


    useEffect(() => {
        fetch('http://localhost:5000/posts')
            .then(res => res.json())
            .then(data => setUserPosts(data))
    }, [userPosts])

    const handleLikeUpdate = id => {
        fetch(`http://localhost:5000/posts/${id}`, {
            method: 'PUT',
            headers: { 'content-type': 'application/json' },
            body: JSON.stringify(like)
        })
            .then(res => res.json())
            .then(data => {
                console.log(data);
            })
        handleLike()
    }


    // const handleLike = (id) => {
    //     const newLike = like + 1;
    //     if (newLike > 0) {
    //         if (newLike === 1) {
    //             setLike(newLike)
    //         }
    //     };

    //     if (newLike > 1) {
    //         setLike(like - 1)
    //     }

    //     fetch(`http://localhost:5000/posts/${id}`, {
    //         method: 'PUT',
    //         headers: { 'content-type': 'application/json' },
    //         body: JSON.stringify(like)
    //     })
    //         .then(res => res.json())
    //         .then(data => {
    //             console.log(data);
    //         })
    // };


    // handle textarea auto resize
    // const textarea = document.getElementById('comment-field')
    // textarea.addEventListener('keyup', e => {
    //     textarea.style.height = `40px`;
    //     const textareaScrollbar = e.target.scrollHeight;
    //     textarea.style.height = `${textareaScrollbar}px`
    // });



    return (
        <div style={{ display: 'flex', flexDirection: "column-reverse" }}>
            {
                userPosts.map(userPost => (
                    <div key={userPost?._id} className="p-4 post-card mb-3">
                        <div className="post-top mb-4 d-flex align-items-center">
                            <div className="user-profile-img rounded-circle me-3">
                                <img className="img-fluid rounded-circle" src="http://www.wpkixx.com/html/winku-dark/images/resources/friend-avatar10.jpg" alt="User_Profile_Picture" />
                            </div>
                            <div>
                                <h6 className="m-0">{userPost?.name}</h6>
                                <p className="m-0"><small>Published: {userPost?.date}</small></p>
                            </div>
                        </div>
                        <div className="post-desc mb-4">
                            <p>{userPost?.postDesc}</p>
                        </div>
                        <div className="post-img mb-4">
                            <img className="img-fluid w-100" src={`data:image/png;base64,${userPost?.image}`} alt="Post_Image" />
                        </div>
                        <div className="post-reacts mb-4">
                            <div className="d-flex justify-content-between">
                                <p className="total-likes d-flex align-items-center"><AiFillLike className="me-1" /> {userPost?.like}</p>
                                <div className="d-flex align-items-center">
                                    <p className="total-likes d-flex align-items-center me-2">0 Comments</p>
                                    <p className="total-likes d-flex align-items-center">0 Shares</p>
                                </div>
                            </div>
                            <hr className="m-0 post-hr" />
                            <Row>
                                <Col className="d-flex align-items-center my-1 justify-content-center post-react-btn-container" md={4} xs={4}>
                                    <div>
                                        <button onClick={() => handleLikeUpdate(userPost?._id)} className="w-100 border-0 post-react-btn d-flex align-items-center">
                                            {like ? <span style={{ color: '#088dcd' }} className="d-flex align-items-center">
                                                <AiFillLike className="like-btn me-2 liked-btn" />Like
                                            </span>
                                                :
                                                <span className="d-flex align-items-center">
                                                    <BiLike className="like-btn me-2" />Like
                                                </span>}
                                        </button>
                                    </div>
                                </Col>
                                <Col className="d-flex align-items-center my-1 justify-content-center post-react-btn-container" md={4} xs={4}>
                                    <div>
                                        <button className="w-100 border-0 post-react-btn d-flex align-items-center">
                                            <span><FaRegCommentDots className="like-btn me-2" /></span>
                                            Comment
                                        </button>
                                    </div>
                                </Col>
                                <Col className="d-flex align-items-center my-1 justify-content-center post-react-btn-container" md={4} xs={4}>
                                    <div>
                                        <button className="w-100 border-0 post-react-btn d-flex align-items-center">
                                            <IoIosShareAlt className="like-btn me-2" />
                                            Share
                                        </button>
                                    </div>
                                </Col>
                            </Row>
                            <hr className="m-0 post-hr" />
                        </div>
                        <div className="post-comment mb-2">
                            <Row className="">
                                <Col xs={2} md={1}>
                                    <div className="user-comment-profile-img mt-1 rounded-pill">
                                        <img className="img-fluid rounded-circle" src="http://www.wpkixx.com/html/winku-dark/images/resources/friend-avatar10.jpg" alt="User_Profile_Picture" />
                                    </div>
                                </Col>
                                <Col className="" xs={10} md={11}>
                                    <div className="comment-box-container  d-flex">
                                        <textarea id="comment-field" rows="4" cols="50" placeholder="Write a Comment" className="bg-transparent text-white pt-2 px-3 border-0 comment-box-filed" />
                                        <div className="text-end align-items-center comment-box-btns d-flex">
                                            <BsCamera className="comment-box-icon" />
                                            <div>
                                                <button className="border-0 text-white comment-btn">Post</button>
                                            </div>
                                        </div>
                                    </div>
                                </Col>
                            </Row>
                        </div>
                    </div >
                ))
            }
        </div>
    );
};

export default UsersPost;
