import { createContext } from "react";
import usePostReact from "../hooks/usePostReact";

const AllPostReactContext = createContext(null);

const AllPostReactProvider = ({ children }) => {
    const likeContext = usePostReact();
    return (
        <AllPostReactContext.Provider value={likeContext}>
            {children}
        </AllPostReactContext.Provider>
    );
};

export default AllPostReactProvider;
