import React, { useState } from 'react';
import { Container } from 'react-bootstrap';
import { BsImage } from 'react-icons/bs';
import { FaMusic, FaVideo, FaCamera } from 'react-icons/fa';
import useAuth from '../../../../hooks/useAuth'
import './PostField.css';

const PostField = () => {
    const { user } = useAuth();
    const [postDesc, setPostDesc] = useState('');
    const [image, setImage] = useState(null);

    const handlePostDesc = e => {
        setPostDesc(e.target.value);
    }
    const handleImage = e => {
        setImage(e.target.files[0]);
    }

    const postSubmit = (e) => {
        e.preventDefault();
        const date = new Date()
        const currentDate = date.toLocaleTimeString();
        const formData = new FormData();
        formData.append('userName', user?.displayName)
        formData.append('userEmail', user?.email)
        formData.append('date', currentDate)
        formData.append('postDesc', postDesc)
        formData.append('image', image)

        fetch('http://localhost:5000/posts', {
            method: 'POST',
            body: formData
        })
            .then(res => res.json())
            .then(result => {
                console.log('success', result);
            })
        console.log(user?.name, user?.email, postDesc, image, currentDate);
    }

    return (
        <div>
            <Container className="post-field d-flex">
                <div className="user-profile rounded-circle">
                    <img className="img-fluid rounded-circle" src="http://www.wpkixx.com/html/winku-dark/images/resources/admin2.jpg" alt="User_Profile_Picture" />
                </div>
                <form onSubmit={postSubmit} className="post-field-container ms-3">
                    <textarea onBlur={handlePostDesc} placeholder="Write something" className="post-field-input bg-transparent border-0 text-white w-100 p-2" />
                    <div className="text-end pe-2 mb-2 text-white">
                        <span className="me-3"><FaMusic /></span>
                        <span className="me-3"><BsImage /></span>
                        <span className="me-3"><FaVideo /></span>
                        <label for="image" className="me-3">
                            <input onChange={handleImage} id="image" accept="image/*" type="file" style={{ display: 'none' }} />
                            <FaCamera />
                        </label>
                        <button type="submit" className="border-0 text-white post-btn">Post</button>
                    </div>
                </form>
            </Container>
        </div>
    );
};

export default PostField;
