import React, { useState } from 'react';
import { Container } from 'react-bootstrap';
import { BsImage } from 'react-icons/bs';
import { FaMusic, FaVideo, FaCamera } from 'react-icons/fa';
import useAuth from '../../../../hooks/useAuth'
import './PostField.css';

const PostField = () => {
    const { user } = useAuth();
    const [postDesc, setPostDesc] = useState('');
    const [image, setImage] = useState(null);

    const handlePostDesc = e => {
        setPostDesc(e.target.value);
    }
    const handleImage = e => {
        setImage(e.target.files[0]);
    }

    const handlePost = () => {
        console.log(postDesc, image);
    }

    return (
        <div>
            <Container className="post-field d-flex">
                <div className="user-profile rounded-circle">
                    <img className="img-fluid rounded-circle" src="http://www.wpkixx.com/html/winku-dark/images/resources/admin2.jpg" alt="User_Profile_Picture" />
                </div>
                <div className="post-field-container ms-3">
                    <textarea onBlur={handlePostDesc} placeholder="Write something" className="post-field-input bg-transparent border-0 text-white w-100 p-2" />
                    <div className="text-end pe-2 mb-2 text-white">
                        <span className="me-3"><FaMusic /></span>
                        <span className="me-3"><BsImage /></span>
                        <span className="me-3"><FaVideo /></span>
                        <label for="image" className="me-3">
                            <input onChange={handleImage} id="image" accept="image/*" type="file" style={{ display: 'none' }} />
                            <FaCamera />
                        </label>
                        <button onClick={handlePost} className="border-0 text-white post-btn">Post</button>
                    </div>
                </div>
            </Container>
        </div>
    );
};

export default PostField;
