import React from 'react';
import { useState } from 'react';
import { Col, Container, Row } from 'react-bootstrap';
import { NavLink, useLocation, useHistory } from 'react-router-dom';
import useAuth from '../../hooks/useAuth';

const Login = () => {
    const { userLogin } = useAuth();
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    const location = useLocation();
    const history = useHistory();


    const handleEmail = e => {
        setEmail(e.target.value);
    }
    const handlePassword = e => {
        setPassword(e.target.value);
    }


    const formOnSubmit = e => {
        e.preventDefault();
        console.log(email, password);
        userLogin(email, password, location, history);
    }

    return (
        <div>
            <Container fluid>
                <Row>
                    <Col className="log-res-left-container d-flex justify-content-center align-items-center" md={6}>
                        <div className="text-white text-center">
                            <h1>Meet Friends</h1>
                            <p>Welcome to Meet Friends social network site its free to use as long as you want</p>
                            <img className="img-fluid my-5" src="http://www.wpkixx.com/html/winku-dark/images/wink.png" alt="Register_Image" />
                            <p>Follow Us on</p>
                        </div>
                    </Col>
                    <Col className="log-res-right-container d-flex justify-content-center align-items-center" md={6}>
                        <div className="form-container">
                            <h2>Login</h2>
                            <p>Donâ€™t use Meet Friends Yet? <span>Take</span> the tour or <span>Join now</span></p>

                            <form onSubmit={formOnSubmit}>
                                <input onBlur={handleEmail} type="email" placeholder="Email Address" className="mt-4 py-2  w-100" />
                                <input onBlur={handlePassword} type="password" placeholder="Password" className="mt-4 py-2  w-100" />
                                <Row className="my-4">
                                    <Col md={6}>
                                        <div className="d-flex align-items-center">
                                            <span className="d-flex align-items-center me-4">
                                                <input name="checked" value="male" style={{ width: '14px', height: '14px', verticalAlign: 'middle' }} type="checkbox" className="me-2" />
                                                <span id="male" style={{ fontSize: '15px', color: '#545454' }}>Remember me</span>
                                            </span>
                                        </div>
                                    </Col>
                                    <Col className="p-0 text-end pe-3" md={6}>
                                        <button style={{ fontSize: '15px', color: '#088dcd' }} className="border-0 p-0 bg-transparent">Forgot Password</button>
                                    </Col>
                                </Row>
                                <button className="w-100 border-0 mt-3 text-white py-2">Login</button>
                            </form>
                            <NavLink className="text-center d-block mt-4" to="/register">Don't have an account?</NavLink>
                        </div>
                    </Col>
                </Row>
            </Container>
        </div >
    );
};

export default Login;
